mapper.select:
SELECT job.job_id,log.status AS exec_status
FROM brz_job job
    LEFT JOIN brz_job_exec_log log
        ON job.job_id = log.job_id
WHERE job.job_id >= #{jobIdLow}
AND job.job_id <= #{jobIdUp}
AND log.status=#{execStatus}
ORDER BY id DESC

SELECT * FROM brz_job_exec_log
WHERE job_id = #{jobId}
ORDER BY id DESC
